import React, { Component } from "react";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import { useForm } from "react-hook-form";

import './loginwindow.scss'


class LoginWindow extends Component {
    constructor(props) {
        super(props);
        const { register, handleSubmit, watch, errors } = useForm();
        const onSubmit = data => console.log(data);

    }

    loginSubmit() {
        fetch("/upnext_videos?start=0&num=10", {
        })
        .then(response => response.json())
        .then(data => {
            this.setState({
                items: this.state.items.concat(data["videos"])
            })
        })
    }
    render() {
        return <div class="login-modal" id="login-modal">
            <div class="modal-content">
                <div class="modal-header">
                    <img src="https://youtube-clone-dev-storage.s3.us-east-2.amazonaws.com/web/icon.png"/>
                    <p>Join Beamr today</p>
                </div>
                <Tabs>
                    <TabList>
                        <Tab>Log In</Tab>
                        <Tab>Sign Up</Tab>
                    </TabList>

                    <TabPanel>
                    <form onSubmit={this.handleSubmit(onSubmit)}>
                        <div class="login-field">
                            <p>Username</p>
                            <input type="text" name="username"></input>
                        </div>
                        <div class="login-field">
                            <p>Password</p>
                            <input type="text" name="password"></input>
                        </div>

                        <button type="submit" class="login-button">Log in</button>
                    </form>
                    </TabPanel>
                    <TabPanel>
                        <div class="login-field">
                            <p>Username</p>
                            <input type="text"></input>
                        </div>
                        <div class="login-field">
                            <p>Password</p>
                            <input type="text"></input>
                        </div>

                        <div class="login-field">
                            <p>Confirm Password</p>
                            <input type="text"></input>
                        </div>

                        <div class="login-field">
                            <p>Email</p>
                            <input type="text"></input>
                        </div>

                        <button type="submit" class="login-button" disabled>Sign Up</button>
                    </TabPanel>
                </Tabs>

            </div>
        </div>
    }
}

export default LoginWindow;
