import React, { Component } from "react";
import './base.css'
import { 
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useLocation
 } from "react-router-dom";
import Home from './Home'
import Watch from './Watch'
import VideoPreview from "./VideoPreview";
import LoginWindow from "./LoginWindow";
import Studio from "./Studio";
import AccountModal from "./base/AccountModal"

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

export function IO() {
  document.getElementById("signup-button").addEventListener("click", function(){ 
    let sidebar_full = document.getElementById("sidebar-full");
    sidebar_full.classList.toggle("expanded");
    sidebar_full.classList.toggle("collapsed");

    let sidebar_mini = document.getElementById("sidebar-mini");
    sidebar_mini.classList.toggle("expanded");
    sidebar_mini.classList.toggle("collapsed");

    let content_container = document.getElementById("content-container");
    content_container.classList.toggle("expanded");
    content_container.classList.toggle("collapsed");
  });

  var login_modal = document.getElementById("login-modal");
  var login_button = document.getElementById("login-button");

  login_button.addEventListener("click", function() {
    login_modal.style.display="flex";
  })

  window.onclick = function(event) {
    if (event.target == login_modal) {
      login_modal.style.display = "none";
    }
  }

  var account_modal = document.getElementById("account-modal");
  var account_button = document.getElementById("account-button")

  account_button.addEventListener("click", function() {
    account_modal.style.display="flex";
  })

  window.onclick = function(event) {
    if (event.target == login_modal) {
      login_modal.style.display = "none";
    }
    if (event.target != account_button) {
      account_modal.style.display = "none";
    }
  }
}

class Main extends Component {

  constructor(props) {
    super(props);
    const loggedIn = true;
  }

  componentDidMount() {

  }

  render() {
  return (
    <Router>
      <div id="main">
        <LoginWindow>

        </LoginWindow>
        <AccountModal />
        <div class="header" id="main-header">
          <div class="header-left">
              <Link to="/"><img class="website-icon" src="https://youtube-clone-dev-storage.s3.us-east-2.amazonaws.com/web/icon.png"/></Link>
              <ul class="nav">
                  <li><Link to="/">Trending</Link></li>
                  <li><Link to="/watch?id=98">Music</Link></li>
                  <li><Link to="/search?q=react">Games</Link></li>
              </ul>
          </div>
          <div class="header-center">
              <div class="searchbar-container">
                  <div class="query-box">
                      <input autocomplete="off" type="text" id="query" name="query" placeholder="Search" required/>
                  </div>
                  <div class="search-button-box">
                      <button>
                      </button>
                  </div>
              </div>
          </div>
          {this.loggedIn
            ? <div class="header-right">
                <button class="ui-button account-button" id="account-button">PROFILE</button>
              </div>
            : <div class="header-right">
                <button class="ui-button signup-button" id="signup-button">Sign Up</button>
                <button class="ui-button login-button" id="login-button">Log In</button>
              </div>
          }
          <div class="header-right">
              {this.loggedIn
                ? <button class="ui-button account-button" id="account-button">PROFILE</button>
                : <button class="ui-button signup-button" id="signup-button">Sign Up</button>
              }

              {this.loggedIn
                ? <span></span>
                : <button class="ui-button login-button" id="login-button">Log In</button>
              }

          </div>
        </div>
      <div class="sidebar-full collapsed" id="sidebar-full" >
        <div class="header">
            <p>SUBSCRIPTIONS</p>
        </div>
        <div class="subscribed-list">
            <ul>
                <li>
                    <div class="subscribed-item">
                        <div class="subscribed-item-left">
                            <img class="subscribed-icon" src=""/>
                        </div>
                        <div class="subscribed-item-right">
                            <p class="channel-name">Channel Name</p>
                            <p class="subscriber-count">9.8m subscribers</p>
                        </div>
                    </div>
                </li>
            </ul>
        </div>
      </div>
      <div class="sidebar-mini expanded" id="sidebar-mini">
          <div class="button-container">
              <button class="expand-button" id="expand-button">                    
                  <div class="button-bg">
                  </div>

              </button>
          </div>

      </div>
      <div class="content-container collapsed" id="content-container">
        <Switch>
          <Route exact path="/" component={Home}/>
          <Route path="/watch" component={Watch}/>
          <Route path="/search">
            <div>
              <VideoPreview 
                static_url="https://youtube-clone-dev-storage.s3.us-east-2.amazonaws.com/web/static.webp"
                animated_url="https://youtube-clone-dev-storage.s3.us-east-2.amazonaws.com/web/animated.webp"
                video_length="9:56"
                id="296"
              />
            </div>
          </Route>
          <Route path='/studio' component={Studio}/>
        </Switch>
      </div>
    </div>
    </Router>
  )
  }
}

export default Main;
