import React, { Component } from "react";
import './home.scss'
import VideoPreview from './VideoPreview'

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            items: []
        }

        this.videos = []
        for (let x = 0; x < 20; x++) {
            //videos.push(<li><VideoPreview /></li>)
            this.videos.push(
            <VideoItem 
                static_url="https://youtube-clone-dev-storage.s3.us-east-2.amazonaws.com/web/static.webp"
                animated_url="https://youtube-clone-dev-storage.s3.us-east-2.amazonaws.com/web/animated.webp"
                video_length="9:56"
                video_url="/watch?id=785"
                profile_image_url=""
                title="New Asus Monitor!"
                channel="randomfrankp"
                view_count="781K"
                upload_date="2 days ago"
            />)
        }

        fetch("/upnext_videos?start=0&num=10", {
        })
        .then(response => response.json())
        .then(data => {
            this.setState({
                items: this.state.items.concat(data["videos"])
            })
        })

    }

    fetchMoreData = () => {
        // a fake async api call like which sends
        // 20 more records in 1.5 secs
        fetch("/upnext_videos?start=0&num=5", {
        })
        .then(response => response.json())
        .then(data => {
            this.setState({
                items: this.state.items.concat(data["videos"])
            })
        })
      };

    render() {
        return <div class="videos-container" id="videos-container">
            <InfiniteScroll
                dataLength={this.state.items.length}
                next={this.fetchMoreData}
                hasMore={true}
                scrollableTarget="video-list-container"
                loader={<h4>Loading...</h4>}>
                    {this.state.items.map((val) => (
                        <RecommendedvideoPreview 
                            title={val.title} 
                            view_count={val.view_count} 
                            upload_date={val.upload_date}
                            video_length={val.video_length}
                            static_url={val.static_url}
                            animated_url={val.animated_url}
                            channel={val.channel}
                        />
                    ))}
                </InfiniteScroll>
        </div>
    }
}

function VideoItem(props) {
    return <div class="video-item">
                <div class="thumbnail-container">
                    <VideoPreview
                        static_url={props.static_url}
                        animated_url={props.animated_url}
                        video_length={props.video_length}
                        video_url={props.video_url}
                    />
                </div>

                <div class="description">
                    <div class="description-left">
                        <img src={props.profile_image_url}/>
                    </div>
                    <div class="description-right">
                        <div class="title">{props.title}</div>
                        <div class="channel">{props.channel}</div>
                        <div class="views_date">{props.view_count} views • {props.upload_date}</div>
                    </div>
                </div>
            </div>;
}

export default Home;
